<!DOCTYPE html>
<head>
<link href="data:image/x-icon;base64," rel="icon" type="image/x-icon" />
	<title>Cloud Leaks Internet Toilet</title>
	<style type="text/css">
		html{height:100%;}
		body {
			background:#0D343A;
			background:-webkit-gradient(linear,0% 0%,0% 100%, from(rgba(13,52,58,1) ),
			to(#000000)  );
			background: -moz-linear-gradient(top, rgba(13,52,58,1) 0%, rgba(0,0,0,1)
			100%);
			overflow:hidden;
			text-align: center;
			font-family: Impact, Haettenschweiler, 'Franklin Gothic Bold', Charcoal, 'Helvetica Inserat', 'Bitstream Vera Sans Bold', 'Arial Black', 'sans serif';
		}

		.drop {
			background:-webkit-gradient(linear,0% 0%,0% 100%, from(rgba(13,52,58,1) ),
			to(rgba(255,255,255,0.6))  );
			background: -moz-linear-gradient(top, rgba(13,52,58,1) 0%,
			rgba(255,255,255,.6) 100%);
			width:1px;
			height:89px;
			position: absolute;
			bottom:200px;
			-webkit-animation: fall .63s linear infinite;
			-moz-animation: fall .63s linear infinite;
		}

		/* animate the drops*/
		@-webkit-keyframes fall {
			to {margin-top:900px;}
		}
		@-moz-keyframes fall {
			to {margin-top:900px;}
		}

		h1 {
			font-size: 4em;
			padding-top: 50px;
			text-shadow: 0 0 10px #fff, 0 0 20px #fff, 0 0 30px #fff, 0 0 40px #ff00de, 0 0 70px #ff00de, 0 0 80px #ff00de, 0 0 100px #ff00de, 0 0 150px #ff00de;
		}
		h2 {
			font-size: 3em;
                        text-shadow: 0 0 10px #fff, 0 0 20px #fff, 0 0 30px #fff, 0 0 40px #ff00de, 0 0 70px #ff00de, 0 0 80px #ff00de, 0 0 100px #ff00de, 0 0 150px #ff00de;
		}
		h3, label {
			font-size: 2em;
                        text-shadow: 0 0 10px #fff, 0 0 20px #fff, 0 0 30px #fff, 0 0 40px #ff00de, 0 0 70px #ff00de, 0 0 80px #ff00de, 0 0 100px #ff00de, 0 0 150px #ff00de;
		}
		h4 {
			font-size: 1em;
                        text-shadow: 0 0 10px #fff, 0 0 20px #fff, 0 0 30px #fff, 0 0 40px #ff00de, 0 0 70px #ff00de, 0 0 80px #ff00de, 0 0 100px #ff00de, 0 0 150px #ff00de;
		}

		a {
			text-decoration: none;
			color: inherit;
		}
		label {
			display: block;	
			float: left;
			clear: left;
			margin-top: 30px;
		}
		input {
			display: block;
			float: right;
			clear: right;
			height: 2em;
			margin-top: 30px;
		    	outline: none;
			border-color: #ff00de;
			box-shadow: 0 0 70px #ff00de;
			font-family: Impact, Haettenschweiler, 'Franklin Gothic Bold', Charcoal, 'Helvetica Inserat', 'Bitstream Vera Sans Bold', 'Arial Black', 'sans serif';
			text-shadow: 0 0 10px #fff, 0 0 20px #fff, 0 0 30px #fff, 0 0 40px #ff00de, 0 0 70px #ff00de, 0 0 80px #ff00de, 0 0 100px #ff00de, 0 0 150px #ff00de;
		}
		form {
			width: 400px;
			margin-left: auto;
			margin-right: auto;
			display: block;
		}
	</style>
	<script	src="//ajax.googleapis.com/ajax/libs/jquery/1.9.1/jquery.min.js"></script>
</head>
<body>
	<section class="rain">
		<h1>Internet of Toilets</h1>
		<h2>Cloud Portal</h2>
		<form method="post">
			<label for="login">Login:</label>
			<input name="login" id="login">
			<label for="passowrd">Password:</label>
			<input name="password" id="password" type="password">
			<input type="submit" value="Submit">
		</form>
	</section>
	<script>
		// number of drops created.
		var nbDrop = randRange(512, 1337); 

		// function to generate a random number range.
		function randRange( minNum, maxNum) {
			return (Math.floor(Math.random() * (maxNum - minNum + 1)) + minNum);
		}

		// function to generate drops
		function createRain() {

			for( i=1;i<nbDrop;i++) {
				var dropLeft = randRange(0,$(document).width());
				var dropTop = randRange(-1000,1400);

				$('.rain').append('<div class="drop" id="drop'+i+'"></div>');
				$('#drop'+i).css('left',dropLeft);
				$('#drop'+i).css('top',dropTop);
			}

		}
		// Make it rain
		createRain();
	</script>
</body>
</html>
